import React, { Component } from "react";
import { Link } from "react-router-dom";
import {
  Button,
  Dimmer,
  Header,
  Icon,
  Image,
  Segment,
  Grid,
  Form,
  Message
} from "semantic-ui-react";
import logo from "../images/Login_Signup/logo.jpg";

class Signup extends Component {
  state = { active: true, username: "", password: "", currentRank: "" };

  handleClickOnDimmer = () => {
    window.history.back();
  };

  handleChange = (event, { name, value }) => {
    this.setState({ [name]: value });
  };

  handleSubmit = () => {
    this.props.handleLogin(this.state);
    this.setState({
      username: "",
      password: "",
      currentRank: ""
    });
  };

  render() {
    const { active } = this.state;

    return (
      <div>
        <Dimmer onClickOutside={this.handleClickOnDimmer} active>
          <div className="login-form">
            <Grid
              textAlign="center"
              style={{ height: "100%" }}
              verticalAlign="middle"
              className="cform"
            >
              <Grid.Row verticalAlign="top">
                <Grid.Column style={{ maxWidth: 525 }}>
                  <Button icon color="grey" textAlign="right">
                    <Icon name="x" />
                  </Button>
                  <Header
                    as="h2"
                    color="yellow"
                    textAlign="center"
                    className="head"
                  >
                    <Image src={logo} /> SIGN UP FOR OVERTRACK
                  </Header>
                  <Form size="large">
                    <Segment stacked>
                      <Form.Input
                        fluid
                        icon="user"
                        iconPosition="left"
                        value={this.state.username}
                        placeholder="Username"
                        name="username"
                        onChange={this.handleChange}
                      />
                      <Form.Input
                        fluid
                        icon="lock"
                        value={this.state.password}
                        iconPosition="left"
                        placeholder="Password"
                        type="password"
                        name="password"
                        onChange={this.handleChange}
                      />
                      <Form.Input
                        fluid
                        value={this.state.currentRank}
                        placeholder="Current Rank ex: 1500"
                        type="CurrentRank"
                        name="currentRank"
                        onChange={this.handleChange}
                      />
                      <Button
                        color="yellow"
                        fluid
                        size="large"
                        onClick={this.handleSubmit}
                      >
                        {" "}
                        Login{" "}
                      </Button>
                    </Segment>
                  </Form>
                </Grid.Column>
              </Grid.Row>
            </Grid>
          </div>
        </Dimmer>
      </div>
    );
  }
}
export default Signup;
