import React, { Component, Suspense, lazy } from "react";
import { Button, Grid, Container, Divider } from "semantic-ui-react";
import ProfileInfo from "../components/ProfileInfo";
import Loaders from "../components/Loaders";
import Graph from "../components/Graphs.js";
const NewsArticle = lazy(() => import("../components/NewsArticle"));

class Profile extends Component {
  state = {
    rankingData: [[0, 0]],
    roleData: [[0, 0]],
    championData: [],
    wonGameData: [],
    lossGameData: []
  };
  componentDidMount() {
    fetch(`http://localhost:3001/api/v1/games/${localStorage.userId}`, {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
        Authorization: `Bearer ${localStorage.token}`
      }
    })
      .then(r => r.json())
      .then(data =>
        this.setState({
          rankingData: [data.map((game, index) => [index, game.new_ranking])],
          roleData: [data.map((game, index) => [index])]
        })
      );
  }

  render() {
    console.log(this.state);
    return (
      <Container className="main ">
        <ProfileInfo />
        <Grid columns={2} className="graphs">
          <Grid.Row className="graphs ">
            <Grid.Column>
              <Graph
                name="Skill Ranking"
                data={this.state.rankingData[0]}
                axes
              />
            </Grid.Column>
          </Grid.Row>
        </Grid>
      </Container>
    );
  }
}

export default Profile;
