import React, { Component } from "react";
import { Card, Grid } from "semantic-ui-react";
import GamePlaceholder from "../components/GamePlaceholder";
import GameCard from "../components/GameCard";
import LazyLoad from "react-lazyload";
import Loaders from "../components/Loaders";

class MatchHistory extends Component {
  state = {
    placeholder: [<GamePlaceholder />],
    array: []
  };
  componentDidMount() {
    fetch(`http://localhost:3001/api/v1/games/${localStorage.userId}`, {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
        Authorization: `Bearer ${localStorage.token}`
      }
    })
      .then(r => r.json())
      .then(r => r.map(game => <GameCard key={game.id} info={game} />))
      .then(r => this.setState({ placeholder: [], array: r.reverse() }));
  }

  render() {
    return (
      <Grid.Column className="gamecards main">
        <LazyLoad placeholder={<Loaders />} once>
          <Card.Group fluid={true}>{this.state.array}</Card.Group>
        </LazyLoad>
      </Grid.Column>
    );
  }
}
export default MatchHistory;
