import React, { Component } from "react";
import { Link } from "react-router-dom";
import {
  Button,
  Dimmer,
  Header,
  Icon,
  Image,
  Segment,
  Grid,
  Form,
  Message
} from "semantic-ui-react";

class Login extends Component {
  state = { active: true };

  handleHide = () => {
    window.history.back();
  };

  render() {
    const { active } = this.state;

    return (
      <div>
        <Dimmer onClickOutside={this.handleHide} active>
          <div className="login-form">
            <Grid
              textAlign="center"
              style={{ height: "100%" }}
              verticalAlign="middle"
              className="cform"
            >
              <Grid.Row verticalAlign="top">
                <Grid.Column style={{ maxWidth: 525 }}>
                  <Header
                    as="h2"
                    color="teal"
                    textAlign="center"
                    className="head"
                  >
                    <Image src="https://image.flaticon.com/icons/svg/174/174295.svg" />{" "}
                    LOG IN TO OVERTRACK
                  </Header>
                  <Form size="large">
                    <Segment stacked>
                      <Form.Input
                        fluid
                        icon="user"
                        iconPosition="left"
                        value={this.state.username}
                        placeholder="Username"
                        name="username"
                        onChange={this.handleChange}
                      />
                      <Form.Input
                        fluid
                        icon="lock"
                        value={this.state.password}
                        iconPosition="left"
                        placeholder="Password"
                        type="password"
                        name="password"
                        onChange={this.handleChange}
                      />

                      <Button
                        color="teal"
                        fluid
                        size="large"
                        onClick={this.handleSubmit}
                      >
                        {" "}
                        Login{" "}
                      </Button>
                    </Segment>
                  </Form>
                  <Message>
                    {" "}
                    New here? <Link to="/signup">Sign Up</Link>{" "}
                  </Message>
                </Grid.Column>
              </Grid.Row>
            </Grid>
          </div>
        </Dimmer>
      </div>
    );
  }
}
export default Login;
