import React, { Component } from "react";
import { Card, Image, Grid, Container, Icon } from "semantic-ui-react";
import "../containers/App.css";

setTimeToTimefFrom = time => {
  const timeFrom = Date.now() - Date.parse(time);
  let seconds = Math.floor((timeFrom / 1000) % 60);
  let minutes = Math.floor((timeFrom / (1000 * 60)) % 60);
  let hours = Math.floor((timeFrom / (1000 * 60 * 60)) % 24);

  hours = hours < 10 ? "0" + hours : hours;
  minutes = minutes < 10 ? "0" + minutes : minutes;
  seconds = seconds < 10 ? "0" + seconds : seconds;
  return hours + ":" + minutes + ":" + seconds;
};
class GameCard extends Component {
  render() {
    console.log(this.props.info);
    return (
      <Card className={this.props.info.result} fluid>
        <Grid columns={4} padded>
          <Grid.Row>
            <Grid.Column>
              <Image
                className="portrait"
                width
                class="image"
                src={require(`../images/portraits/${
                  this.props.info.hero_one
                }-select-portrait.png`)}
              />
              <Container as="h3" textAlign="center">
                {this.props.info.hero_one}
              </Container>
              <Container as="h3" textAlign="center">
                Primary Hero
              </Container>
            </Grid.Column>

            <Grid.Column>
              <Image
                className="portrait"
                class="image"
                src={require(`../images/portraits/${
                  this.props.info.hero_two
                }-select-portrait.png`)}
              />
              <Container as="h3" textAlign="center">
                {this.props.info.hero_two}
              </Container>
              <Container as="h3" textAlign="center">
                Secondary Hero
              </Container>
            </Grid.Column>
            <Grid.Column width="eight">
              <Image
                className="map"
                class="image"
                src={require(`../images/maps/${this.props.info.map}_map.jpg`)}
              />
              <Container as="h3" textAlign="center">
                {this.props.info.map}
              </Container>
              <Container as="h3" textAlign="center">
                Map
              </Container>
              <Container as="h2" textAlign="left">
                Played {setTimeToTimefFrom(this.props.info.created_at)} ago
              </Container>
              <Icon size="huge" name="caret down" className="show" />
            </Grid.Column>
          </Grid.Row>
        </Grid>
      </Card>
    );
  }
}

export default GameCard;
