import React, { Component } from "react";
import { Card, Image, Grid, Container } from "semantic-ui-react";
import "../containers/App.css";

class GameCard extends Component {
  render() {
    console.log(this.props.info);
    return (
      <Card className={this.props.info.result} fluid>
        <Grid columns={5} padded>
          <Grid.Row>
            <Grid.Column>
              <Image
                className="portrait"
                width
                class="image"
                src={`https://d1u1mce87gyfbn.cloudfront.net/hero/${
                  this.props.info.hero_one
                }/hero-select-portrait.png`}
              />
              <Container as="h3" textAlign="center">
                {this.props.info.hero_one}
              </Container>
              <Container as="h3" textAlign="center">
                Primary Hero
              </Container>
            </Grid.Column>

            <Grid.Column>
              <Image
                className="portrait"
                class="image"
                src={`https://d1u1mce87gyfbn.cloudfront.net/hero/${
                  this.props.info.hero_two
                }/hero-select-portrait.png`}
              />
              <Container as="h3" textAlign="center">
                {this.props.info.hero_two}
              </Container>
              <Container as="h3" textAlign="center">
                Secondary Hero
              </Container>
            </Grid.Column>
            <Grid.Column width="seven">
              <Image
                className="map"
                class="image"
                src={require(`../images/maps/${this.props.info.map}_map.jpg`)}
              />
            </Grid.Column>

            <Grid.Column>hey</Grid.Column>
          </Grid.Row>
        </Grid>
      </Card>
    );
  }
}

export default GameCard;
