import React, { Component } from "react";
import { Card, Image } from "semantic-ui-react";
import Portraits from "";

class GameCard extends Component {
  componentDidMount() {
    var req = require.context("../images/portraits", false, /.*\.png$/);
    req.keys().forEach(function(key) {
      req(key);
    });
  }

  render() {
    return (
      <Card fluid>
        <Image src={`${this.props.info.hero_one}-select-portrait.png`} />
      </Card>
    );
  }
}

export default GameCard;
